// This approach handles both single tweets and collections
// It focuses on properly formatting the output for n8n

// 1. Log the raw input structure for debugging
console.log("Raw input:", JSON.stringify($input.all(), null, 2));

// 2. Date‚Äêformatting helper
function formatDate(dateString) {
  if (!dateString) return "";
  try {
    const d = new Date(dateString);
    return d.toLocaleString("en-US", {
      year:   "numeric",
      month:  "long",
      day:    "numeric",
      hour:   "2-digit",
      minute: "2-digit",
    });
  } catch {
    return dateString;
  }
}

// 3. Extract the array of tweets from the HTTP Request output
const allInput = $input.all();
if (!allInput.length || !allInput[0].json.tweets) {
  // No tweets array found ‚Üí return empty
  return [];
}
const tweetArray = allInput[0].json.tweets;

// 4. Transform each tweet and add status/tweet fields
const result = tweetArray.map((t) => {
  const json = t || {};
  const metrics = json.public_metrics || {};

  const tweetData = {
    tweetId:      json.id || "",
    url:          json.twitterUrl || json.url || "",
    content:      json.text || "",
    likeCount:    metrics.like_count       ?? 0,
    viewCount:    metrics.impression_count ?? 0,
    retweetCount: metrics.retweet_count    ?? 0,
    quoteCount:   metrics.quote_count      ?? 0,
    replyCount:   metrics.reply_count      ?? 0,
    createdAt:    formatDate(json.createdAt || json.created_at),
    status:       "waiting",    // new column, default ‚Äúwaiting‚Äù
    tweet:        ""            // new column, will be blank until a reply is posted
  };

  console.log("Processing tweet:", tweetData.tweetId);
  return { json: tweetData };
});

// 5. Return the mapped array back to n8n
return result;

/**
 * n8n Function Node
 * -----------------
 * Input: Merged items (each containing one tweet + one character record)
 * Output: For each item, produce:
 *   - `prompt`: a detailed instruction for the AI, embedding character attributes and the tweet content
 *   - `user_message`: the tweet‚Äôs text (used as the user‚Äôs message for the LLM)
 */

const items = $input.all();

const result = items.map((item) => {
  const data = item.json;

  // Extract tweet fields
  const tweetId    = data.tweetId || "";
  const tweetText  = data.content  || "";
  const tweetURL   = data.url      || "";

  // Extract character fields
  const name       = data.name       || "Unnamed";
  const age        = data.age        || "";
  const backstory  = data.backstory  || "";
  const personality = data.personality || {};
  const passions   = data.passions   || {};
  const goals      = data.goals      || "";
  const behavior   = data.behavior   || {};
  const tasks      = data.tasks      || {};

  // Serialize JSONB fields into human-readable snippets
  // (Assumes personality, passions, behavior, tasks are plain objects)
  const personalityText = Object.entries(personality)
    .map(([k, v]) => `${k}: ${v}`)
    .join("; ");
  const passionsText = Object.entries(passions)
    .map(([k, v]) => `${k}: ${v}`)
    .join("; ");
  const behaviorText = Object.entries(behavior)
    .map(([k, v]) => `${k}: ${v}`)
    .join("; ");
  const tasksText = Object.entries(tasks)
    .map(([k, v]) => `${k}: ${v}`)
    .join("; ");

  // Build the LLM prompt
  const prompt = `
You are ${name}, age ${age}. Backstory: ${backstory}
Personality: ${personalityText}
Passions: ${passionsText}
Goals: ${goals}
Behavior: ${behaviorText}
Tasks: ${tasksText}

You see the following tweet:
"${tweetText}"

As ${name}, craft a reply that fits your personality and goals. Make sure to:
‚Ä¢ Sound like you (reference your passions if it makes sense)
‚Ä¢ Keep it concise (under 100 characters if possible)
‚Ä¢ Use your unique tone and style
‚Ä¢ Optionally include an emoji or two, if it matches your vibe

Now generate your response.`;

  return {
    json: {
      prompt: prompt.trim(),
      user_message: tweetText
    }
  };
});

return result;

